mapper.select:
select * from (select @RN:=@RN+1 as rnum, a.* from (select a.*, c.c_name, s.sf_name, t.tr_name from advice_t a,course_t c, staff_t s, trainee_t t
so_status = 0 and a.c_idx= c.c_idx and c.sf_idx = s.sf_idx and a.tr_idx = t.tr_idx and sf_name like concat('%', #{value}, '%') and so_day like concat('%', #{value}, '%') and c_name like concat('%', #{value}, '%') and tr_name like concat('%', #{value}, '%') and start_date like concat(#{year},'%') and
order by so_idx desc )a ,(select @RN:=0) b) c where c.rnum between ${begin} and ${end}

mapper.update:
update advice_t
set so_status = 1
where so_idx = #{so_idx}

update advice_t
ss_day = #{so_day}, so_menu = #{so_menu}, so_subject = #{so_subject}, so_pd = #{so_pd}, so_tname = #{so_tname}
where so_idx = #{so_idx}

mapper.select:
select count(*) from (select @RN:=@RN+1 as rnum, a.* from (select a.*, c.c_name, s.sf_name, t.tr_name from advice_t a,course_t c, staff_t s, trainee_t t
so_status = 0 and a.c_idx= c.c_idx and c.sf_idx = s.sf_idx and a.tr_idx = t.tr_idx and sf_name like concat('%', #{value}, '%') and so_day like concat('%', #{value}, '%') and c_name like concat('%', #{value}, '%') and tr_name like concat('%', #{value}, '%') and start_date like concat(#{year},'%') and
)a ,(select @RN:=0) b) c
