mapper.select:
select * from (select @RN:=@RN+1 as rnum, a.* from (select a.*, c.c_name, s.sf_name, t.tr_name from advice_t a,course_t c, staff_t s, trainee_t
status = 0 and a.c_idx= c.c_idx and c.sf_idx = s.sf_idx and a.tr_idx = t.tr_idx and sf_name =#{value} and ss_day like concat('%', {value}, '%') and c_name=#{value} and tr_name=#{value} and start_date like concat(#{year},'%') and
)a ,(select @RN:=0) b) c where c.rnum between ${begin} and ${end}

mapper.update:
update advice_t
set status = 1
where so_idx = #{so_idx}

    update advice_t

        so_idx = #{so_idx},
        ss_day = #{ss_day},
        tr_idx = #{tr_idx},
        course_fee = #{course_fee},
        ss_menu = #{ss_menu},
        ss_pp = #{ss_pp}
        subject = #{subject}
        ss_pd = #{ss_pd}
        c_idx = #{c_idx}


	    where so_idx = #{so_idx}

mapper.select:
select count(*) from (select @RN:=@RN+1 as rnum, a.* from (select a.*, c.c_name, s.sf_name, t.tr_name from advice_t a,course_t c, staff_t s, trainee_t
status = 0 and a.c_idx= c.c_idx and c.sf_idx = s.sf_idx and a.tr_idx = t.tr_idx and sf_name =#{value} and ss_day like concat('%', {value}, '%') and c_name=#{value} and tr_name=#{value} and start_date like concat(#{year},'%') and
)a ,(select @RN:=0) b) c
