mapper.select:
select * from (select @RN:=@RN+1 as rnum, a.* from (select c.*,r.r_name,s.sf_name,ct.ct_name from course_t c,room_t r,staff_t s,course_type_t ct
c_status = 0 and c.ct_idx= ct.ct_idx and c.r_idx=r.r_idx and s.sf_idx = c.sf_idx and s.sf_name like concat('%',#{value},'%') and ct.ct_name like concat('%',#{value},'%') and c.c_name like concat('%',#{value},'%') and start_date like concat(#{year},'%') and
order by c_idx desc )a ,(select @RN:=0) b) c where c.rnum between ${begin} and ${end}

mapper.update:
update course_t
set c_status = 1
where c_idx = #{c_idx}

update course_t
c_name = #{c_name}, start_date = #{start_date}, end_date = #{end_date}, course_fee = #{course_fee}, c_peo_num = #{c_peo_num}, c_round_num = #{c_round_num}, c_day = #{c_day}, ct_idx = #{ct_idx}, sf_idx = #{sf_idx}
where c_idx = #{c_idx}

mapper.select:
select count(*) from (select @RN:=@RN+1 as rnum, a.* from (select sb.*, c.*,r.r_name,s.sf_name,ct.ct_name from course_t c,room_t r,staff_t s,course_type_t ct,subject_t sb
c_status = 0 and c.ct_idx= ct.ct_idx and c.r_idx=r.r_idx and s.sf_idx = c.sf_idx and sb.c_idx= c.c_idx s.sf_name like concat('%',#{value},'%') and ct.ct_name like concat('%',#{value},'%') and c.c_name like concat('%',#{value},'%') and start_date like concat(#{year},'%') and
)a ,(select @RN:=0) b) c

select count(*) from (select @RN:=@RN+1 as rnum, a.* from (select c.*,r.r_name,s.sf_name,ct.ct_name from course_t c,room_t r,staff_t s,course_type_t ct
c.c_status = 0 and c.ct_idx= ct.ct_idx and c.r_idx=r.r_idx and s.sf_idx = c.sf_idx and (c.start_date like concat (#{year},'%') or c.end_date like concat (#{year},'%'))
)a ,(select @RN:=0) b) c
