mapper.update:
update evaluation_status
es_name = #{es_name}, es_type = #{es_type}, es_examStatus = #{es_examStatus}, es_gradingStatus = #{es_gradingStatus}, sf_idx = #{sf_idx}, es_date = #{es_date}, es_time = #{es_time}, es_totalScore = #{es_totalScore}, es_level = #{es_level}, es_num_question = #{es_num_question}, sk_idx = #{sk_idx}, s_idx = #{s_idx},
where es_idx = #{es_idx}

update evaluation_status
set es_status = 1
where es_idx = #{es_idx}
