mapper.select:
select * from course_t
where c_status = 0 and ct_idx = #{ct_idx}
order by start_date desc

select * from course_t
where c_status = 0 and c_name like concat('%', #{c_name}, '%') and ct_idx = #{ct_idx}
order by start_date desc

mapper.update:
update member_t
m_pw = #{m_pw}, m_email = #{m_email}, m_phone = #{m_phone}, m_name = #{m_name}, m_addr = #{m_addr}
where m_id = #{m_id}

        update trainee_t

            tr_name = #{tr_name},
			tr_phone = #{tr_phone},
            tr_addr = #{tr_addr}

        where tr_idx = #{tr_idx}
